// Função para adicionar a regra de reescrita
function custom_rewrite_rule() {
    $slug = get_option('cda_slug', 'downloads');
    add_rewrite_rule('^'.$slug.'/([^/]+)/acesso-liberado/?$', 'index.php?'.$slug.'=$matches[1]', 'top');
}
add_action('init', 'custom_rewrite_rule');

// Função para registrar variáveis de query
function register_query_vars($vars) {
    $slug = get_option('cda_slug', 'downloads');
    $vars[] = $slug;
    return $vars;
}
add_filter('query_vars', 'register_query_vars');

// Função para desabilitar redirecionamentos canônicos para URLs personalizadas
function disable_canonical_redirects_for_custom_urls($redirect_url) {
    $slug = get_option('cda_slug', 'downloads');
    if (get_query_var($slug, false)) {
        return false;
    }
    return $redirect_url;
}
add_filter('redirect_canonical', 'disable_canonical_redirects_for_custom_urls', 10, 2);

// Função para gerar token
function generate_token($email) {
    $time = time();
    $data = $email . '##' . $time;
    return base64_encode($data);
}

// Função para validar o token
function validate_token($token) {
    $decoded_data = base64_decode($token);
    if ($decoded_data === false) {
        return false;
    }

    list($email, $timestamp) = explode('##', $decoded_data);

    // Valide o tempo (exemplo: token válido por 1 hora)
    $current_time = time();
    if ($current_time - $timestamp > 3600) {
        return false;
    }
    return true;
}

// Função para gerar o link de retorno
function generate_return_link() {
    $current_url = (is_ssl() ? 'https://' : 'http://') . $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];
    $base_url = preg_replace('/\/acesso-liberado\/?(\?.*)?$/', '', $current_url);
    return esc_url($base_url);
}
add_shortcode('return_link', 'generate_return_link');

// Função para verificar acesso e validar token
function check_acesso_liberado_and_validate_token() {
    $slug = get_option('cda_slug', 'downloads');
    $current_url = (is_ssl() ? 'https://' : 'http://') . $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];

    if (strpos($current_url, 'acesso-liberado') !== false) {
        if (is_user_logged_in() && current_user_can('administrator')) {
            return 'liberado';
        }

        $token = isset($_GET['token']) ? sanitize_text_field($_GET['token']) : '';

        if (!empty($token) && validate_token($token)) {
            return 'liberado';
        }

        return 'negado';
    }

    return 'neutro';
}
add_shortcode('check_acesso_liberado', 'check_acesso_liberado_and_validate_token');

// Função para adicionar JavaScript apenas em post types de "downloads"
function enqueue_custom_script() {
    $slug = get_option('cda_slug', 'downloads');
    $base_url = get_option('cda_base_url', get_site_url());
    $slugPost = get_post_field('post_name', get_post());

    if (is_singular($slug)) {
        ?>
        <script>
        document.addEventListener('DOMContentLoaded', function () {
            document.querySelectorAll('.elementor-form').forEach(function (form) {
                form.addEventListener('submit', function (event) {
                    event.preventDefault();

                    var emailField = form.querySelector('input[type="email"]');
                    var slugField = form.querySelector('input[name="form_fields[slug]"]');
                    if (emailField) {
                        var email = emailField.value;
                        var token = btoa(email + '##' + Math.floor(Date.now() / 1000));
                        var redirectUrl = '<?php echo esc_js($base_url.'/'.$slug.'/'.$slugPost); ?>/acesso-liberado/?token=' + token;

                        window.location.href = redirectUrl;
                    }
                });
            });
        });
        </script>
        <?php
    }
}
add_action('wp_footer', 'enqueue_custom_script');

// Função para adicionar a página de configuração ao menu de administração
function cda_add_admin_menu() {
    add_options_page('Download Restrito', 'Download Restrito', 'manage_options', 'cda_settings', 'cda_options_page');
}
add_action('admin_menu', 'cda_add_admin_menu');

// Função para registrar as configurações
function cda_settings_init() {
    register_setting('cda_settings', 'cda_base_url');
    register_setting('cda_settings', 'cda_slug');

    add_settings_section(
        'cda_settings_section',
        __('Configurações do Plugin', 'cda'),
        null,
        'cda_settings'
    );

    add_settings_field(
        'cda_base_url',
        __('URL Base', 'cda'),
        'cda_base_url_render',
        'cda_settings',
        'cda_settings_section'
    );

    add_settings_field(
        'cda_slug',
        __('Slug', 'cda'),
        'cda_slug_render',
        'cda_settings',
        'cda_settings_section'
    );
}
add_action('admin_init', 'cda_settings_init');

